// 监听页面的滚动 一般搭配函数节流
let throttle = (fn, delay) => {

    let atFirst = true;
    let timmer = null;
    let preDate = null;

    return function (...arg) {
        let _this = this;

        timmer && clearTimeout(timmer);

        if (atFirst) {
            fn.apply(_this, arg);

            preDate = Date.now();
            atFirst = false;
            return;
        }

        if (Date.now() - preDate >= delay) {

            fn.apply(_this, arg);
            preDate = Date.now();

        } else {
            timmer = setTimeout(() => {
                fn.apply(_this, arg);
                preDate = timmer = null;
                atFirst = true;
            }, delay);
        }
    }
}

